sys_variable_value.value:
(function execute(inputs, outputs) {
    var taskGr = new GlideRecord(inputs.task_class.getValue('name'));
    taskGr.initialize();
    taskGr.setValue('parent', inputs.parent.getUniqueValue());
    taskGr.setValue('short_description', inputs.form.getValue('name'));
    taskGr.setValue('state', '10');
    taskGr.setValue('description', inputs.form.getValue('description'));
    taskGr.setValue('correlation_id', inputs.form.getUniqueValue());
    if(!gs.nil(inputs.due_date)){
        taskGr.setValue('due_date', inputs.due_date);
    }
    if (!gs.nil(inputs.assignment_group)) {
        taskGr.setValue('assignment_group', inputs.assignment_group)
    }
    if (!gs.nil(inputs.assigned_to)) {

        //Check to see if the assigned to user is in the group
        if (!gs.nil(inputs.assignment_group)) {
            var grMemberGr = new GlideRecord('sys_user_grmember');
            grMemberGr.addQuery('group', inputs.assignment_group.getUniqueValue());
            grMemberGr.addQuery('user', inputs.assigned_to.getUniqueValue());
            grMemberGr.query();
            if (grMemberGr.next()) {
                taskGr.setValue('assigned_to', inputs.assigned_to.getUniqueValue());
            }
        } else {
            taskGr.setValue('assigned_to', inputs.assigned_to.getUniqueValue());
        }

    }

    outputs.task_id = taskGr.insert();


})(inputs, outputs);

