x_509925_fc_page.attributes:
(function (params) {

	function initialize() {
		var model = {
			condition: true,
			user: gs.getUserDisplayName()
		};
		return model;
	}


	return {
		initialize: initialize
	};

})(params);

x_509925_fc_page.client_scripts:
function execute(helpers) {

    function carousel() {
        return {
            activeSlide: 0,

            next() {
                this.activeSlide = (this.activeSlide + 1) % 3; // Assuming 3 slides
            },
            previous() {
                this.activeSlide = (this.activeSlide + 3 - 1) % 3; // Assuming 3 slides
            }
        }
    }
    return {
        carousel: carousel
    }
}

