x_509925_fc_page.attributes:
(function (params) {

    function initialize() {
        try {
            var user = gs.getUser();
            var model = {
                condition: true,
                user: gs.getUserDisplayName(),
                avatar: user.getEmail()
            };
            //new FCDepartmentRepository().demo();
            return model;
        } catch (e) {
            new FCPageRepository().throwError(e, params.id, arguments.callee.name);
        }
    }


    return {
        initialize: initialize
    };

})(params);

x_509925_fc_page.client_scripts:
function execute(helpers) {

    function navigate(url) {
        helpers.router.navigateByUrl(url);
    }
    return {
        navigate: navigate
    };
}

