x_509925_fc_page.attributes:
(function (params) {

	function initialize() {
		var model = {
			condition: true,
            query_name: 'active_incidents'
		};

		return model;
	}

    function sayHello(name){
        return `Hello ${name}!`;
    }


	return {
		initialize: initialize,
        sayHello: sayHello
	};

})(params);

x_509925_fc_page.client_scripts:
function execute(helpers){
    async function sayHello(name){
        var response = await helpers.service.call('sayHello', [name]);
        helpers.service.snackbar(response);
    }

    return {
        sayHello: sayHello
    };
}
