x_509925_fc_page.attributes:
(function (params) {

    function initialize() {
        var model = {
            condition: true,
            images: [
                'https://images.pexels.com/photos/1583391/pexels-photo-1583391.jpeg?auto=compress&cs=tinysrgb&w=600',
                'https://images.pexels.com/photos/27902286/pexels-photo-27902286/free-photo-of-person-pouring-coffee.jpeg?auto=compress&cs=tinysrgb&w=600',
                'https://images.pexels.com/photos/14540991/pexels-photo-14540991.jpeg?auto=compress&cs=tinysrgb&w=600'
                ],
            knowledge_bases: new FCKnowledgeRepository().getActiveKnowledgeBases(true)
        };

        return model;
    }

    return {
        initialize: initialize
    };

})(params);

x_509925_fc_page.client_scripts:
function execute(helpers) {

    function gotoUrl(url) {
        helpers.service.navigate(url);
    }

    function openBase(id, categoryId){
        gotoUrl(`?id=knowledge_base&kb_knowledge_base=${id}${categoryId ? ('&kb_category=' + categoryId) : ''}`);
    }

    async function getCount(){
        var response = await helpers.service.call('getCount');
        return response;
    }

    return {
        gotoUrl: gotoUrl,
        openBase: openBase,
        getCount: getCount
    };
}

